import React,{ useEffect } from 'react';
import { Row, Col }  from 'react-bootstrap';
import Product from '../components/Product';
import Message from '../components/Message';
import Loader from '../components/Loader';
//import bikes from '../bikes';
//import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { listBikes } from '../actions/bikeAction';

const BikeStore = ({match}) => {
    const keyword = match.params.keyword
    //const [bikes, setBikes] = useState([]);
    const dispatch = useDispatch();

    const bikeList = useSelector(state => state.bikeList)
    const { loading, error, bikes} = bikeList

    useEffect(()=>{
        dispatch(listBikes(keyword))
    },[dispatch, keyword])

    return (
    <>
        <h1>Bike Store</h1>
        { loading ? (
                <Loader />
            ): error ?(
                <Message variant='danger'>{error}</Message>
            ):(
                <Row>
                    {bikes.map(bike => (
                            <Col key={bike._id} sm={12} md={6} xl={4}>
                                <Product bike={bike} />
                            </Col>
                            )
                        )
                    }
                </Row>
            )
        }
    </>
    )
}

export default BikeStore;
