import React,{ useState } from 'react'
import { LinkContainer } from "react-router-bootstrap";
import { Col, Button, Navbar, Nav, NavDropdown } from 'react-bootstrap'
import { useDispatch, useSelector}  from 'react-redux'
import { logout } from '../actions/userAction'

const AdminDashboard = () => {
    const [active, setActive] = useState('')

    const dispatch = useDispatch()

    const userLogin = useSelector(state => state.userLogin)
    const { userInfo } = userLogin

    const logoutHandler = () =>{
        dispatch(logout())
    }

    const sidbarHandler = (e) =>{
        e.preventDefault()
        if(active === '')
            setActive('active')
        else
            setActive('')
    }


    return (
      <>
        <div className='wrap-sidebar'>
        <Navbar id='sidebar' className={`${active}`}>
          <Col className='sidebar-button'>
            <Button 
              type='button' 
              variant='light'
              id="sidebarCollapse" 
              onClick={sidbarHandler}
            ><i className={active ?'fa fa-bars':'fa fa-times'}></i></Button>
          </Col>

          {/* <Navbar.Brand href="/" className="brand-logo">B</Navbar.Brand> */}
          <Nav className="px-4 sidebar-navs">
            <LinkContainer to='/'><i className='fas fa-users'></i> Users </LinkContainer>
            
          </Nav>
        </Navbar>
        </div>
    


        
      </>
    )
}

export default AdminDashboard
