import React from 'react'
import { Link } from 'react-router-dom'
import { Carousel, Image } from 'react-bootstrap'
import Loader from './Loader'
import Message from './Message'



const CarouselBikes = () => {
    const data = [ 
        { 
            _id: 1,
            image: '/images/1.jpg',
            name: 'Montain Bike'
        },
        { 
            _id: 2,
            image: '/images/2.jpg',
            name: 'Montain Bike'
        },
        { 
            _id: 3,
            image: '/images/3.jpg',
            name: 'Montain Bike'
        }
    ]

    return (
        <Carousel>
            {data.map(bike => (
                <Carousel.Item key={bike._id} className='text-center'>
                    <Link to={`/bikes/${bike._id}`}>
                        <Image src={bike.image} alt={bike.name} fluid />
                        <Carousel.Caption className='carousel-caption'>
                            <h2>{bike.name}</h2>
                        </Carousel.Caption>
                    </Link>
                </Carousel.Item>
            ))}
        </Carousel>
    )
}

export default CarouselBikes
