import React,{ useEffect} from 'react'
import { LinkContainer } from 'react-router-bootstrap'
import { Table, Button, Row, Col } from 'react-bootstrap'
import { useDispatch, useSelector } from 'react-redux'
import Message from '../components/Message'
import Loader from '../components/Loader'
import { listBikes, deleteBike } from '../actions/bikeAction'
import { BIKE_CREATE_RESET } from '../constants/bikeConstants'

const BikeListView = ({history}) => {
    const dispatch = useDispatch()

    const bikeList = useSelector(state => state.bikeList)
    const { loading, error, bikes } = bikeList

    const userLogin = useSelector(state => state.userLogin)
    const { userInfo } = userLogin

    const bikeDelete = useSelector(state => state.bikeDelete)
    const { loading: loadingDelete, error: errorDelete, success: successDelete } = bikeDelete

    const bikeCreate = useSelector(state => state.bikeCreate)
    const{  success: successCreate, bike: createdBike} = bikeCreate

    useEffect(()=>{
        dispatch({type: BIKE_CREATE_RESET})
        if(!userInfo || !userInfo.isAdmin){
            history.push('/login')
        }

        if(successCreate){
            history.push(`/admin/bikelist`)
        }else{
            dispatch(listBikes())
        }
        
    },[dispatch, history, userInfo, successCreate, createdBike, successDelete])

    const createBikeHandler = () =>{
        history.push('/admin/bike/create')
    }

    const deleteHandler = (id) =>{
        if(window.confirm('Are you sure?')){
            dispatch(deleteBike(id))
        }
    }
    return (
        <>
            <Row>
                <Col className="text-right">
                    <Button className='my-3' onClick={createBikeHandler}>
                        <i className='fas fa-plus'>Create Bike</i>
                    </Button>
                </Col>
            </Row>
            <h2>Bikes</h2>
            {loadingDelete && <Loader />}
            {errorDelete && <Message variant='danger'>{errorDelete}</Message>}
            {loading ? <Loader /> : error ? <Message variant='danger'>{error}</Message>:(
                <Table striped bordered hover responsive className='table-sm'>
                    <thead>
                        <tr>
                            <th>ID</th>
                            <th>NAME</th>
                            <th>BRAND</th>
                            <th>CATEGORY</th>
                            <th>PRICE</th>
                            <th>IN STOCK</th>
                            <th>ACTION</th>
                        </tr>
                    </thead>
                    <tbody>
                        {bikes.map(bike => (
                            <tr key={bike._id}>
                                <td>{bike._id}</td>
                                <td>{bike.name}</td>
                                <td>{bike.brand}</td>
                                <td>{bike.category}</td>
                                <td>{bike.price}</td>
                                <td>{bike.countInStock}</td>
                                <td>
                                    <LinkContainer to={`/admin/bike/${bike._id}/edit`}>
                                        <Button variant='light' className='btn-sm'><i className='fas fa-edit'></i></Button>
                                    </LinkContainer>
                                    <Button variant='danger' className='btn-sm' onClick={() => deleteHandler(bike._id)}>
                                        <i className='fas fa-trash'></i>
                                    </Button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </Table>
            )}
            
        </>
    )
}

export default BikeListView
