import React,{ useEffect } from 'react'
import { Row, Col }  from 'react-bootstrap';
import Product from '../components/Product';
import Message from '../components/Message';
import Loader from '../components/Loader';
//import axios from 'axios';
//import bikes from '../bikes';
import { useDispatch, useSelector } from 'react-redux';
import { listTopBikes } from '../actions/bikeAction';

const HomeView = () => {
    // const [bikes, setBikes] = useState([]);

    // useEffect(()=>{
    //     const fetchBikes = async()=>{
    //         const{data} = await axios.get('/api/bikes');
    //         const raitedBikes = data.filter((b) => b.rating === 5);
    //         setBikes(raitedBikes);
    //     }
    //     fetchBikes()
    // },[])

    const dispatch = useDispatch();

    const bikeList = useSelector(state => state.bikeList)
    const { loading, error, bikes} = bikeList

    useEffect(()=>{
        dispatch(listTopBikes())
    },[dispatch])

    
    return (
    <>
        <h1>Top Raited Bikes</h1>
        { loading ? (
                <Loader />
            ): error ?(
                <Message variant='danger'>{error}</Message>
            ):(
                <Row>
                    {bikes.map(bike => (
                            <Col key={bike._id} sm={12} md={6} xl={4}>
                                <Product bike={bike} />
                            </Col>
                            )
                        )
                    }
                </Row>
            )
        }
    </>
    )
}

export default HomeView
