import React,{ useState, useEffect } from 'react'
import { Bar } from 'react-chartjs-2'
import Loader from '../components/Loader';
import { useDispatch, useSelector } from 'react-redux';
import { getDashboardOrdersData } from '../actions/dashboardAction'

const data = {
    labels: ['Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa', 'Su'],
    datasets: [
      {
        label: '# orders',
        data: [12, 19, 3, 9, 6, 3, 16],
        backgroundColor: [
          'rgba(255, 99, 132, 0.2)',
          'rgba(54, 162, 235, 0.2)',
          'rgba(255, 206, 86, 0.2)',
          'rgba(75, 192, 192, 0.2)',
          'rgba(153, 102, 255, 0.2)',
          'rgba(255, 159, 64, 0.2)',
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
          'rgba(255, 159, 64, 1)',
        ],
        borderWidth: 1,
      },
    ],
  }
  
  const options = {
    scales: {
      yAxes: [
        {
          ticks: {
            beginAtZero: true,
          },
        },
      ],
    },
  }

const WeekOrdersPie = () => {
    const [labelsArray, setLabelsArray] = useState([])
    const colors = ['#CA5C00', '#E58943', '#F1B166','#F1D1A2','#FCE4D8','#EBD3C2']
    const dispatch = useDispatch();

    const dashboardOrdersData = useSelector(state => state.dashboardOrdersData)
    const { loading, ordersdata} = dashboardOrdersData
    
    

    useEffect(()=>{
        dispatch(getDashboardOrdersData())
        
    },[dispatch])

    if(!loading){
      ordersdata.map(o => {
        setLabelsArray([... o.date])
      })
    }

    return (
        <> 
          <div className='header'>
            <h3 className='title'>This week orders</h3>
          </div>
          { loading ? ( <Loader />) :(
            
            <Bar 
              data={{
                labels: [ordersdata.map(o => o.date)],
              }} 
              options={options} 
            />
          )}
        </>
    )
}

export default WeekOrdersPie
