import React,{ useEffect } from 'react'
import { Bar } from 'react-chartjs-2'
import Loader from '../components/Loader';
import { useDispatch, useSelector } from 'react-redux';
import { getDashboardOrdersData } from '../actions/dashboardAction'

const WeekOrdersPie = () => {
    const labelsArray  = ['Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa', 'Su']
    const colors = ['#CA5C00', '#E58943', '#F1B166','#F1D1A2','#FCE4D8','#EBD3C2']
    const options = {
      scales: {
        yAxes: [
          {
            ticks: {
              beginAtZero: true,
            },
          },
        ],
      },
    }
    const dispatch = useDispatch();

    const dashboardOrdersData = useSelector(state => state.dashboardOrdersData)
    const { loading, ordersdata} = dashboardOrdersData
    
    

    useEffect(()=>{
      dispatch(getDashboardOrdersData())
      // eslint-disable-next-line
    },[dispatch])

    return (
        <> 
          <div className='header text-center'>
            <h3 className='title'>This week orders</h3>
          </div>
          { loading ? ( <Loader />) :(
            
            <Bar 
              data={{
                labels: labelsArray,
                datasets: [{
                  label: '# orders',
                  data: ordersdata,
                  backgroundColor: colors,
                  borderColor: colors,
                  borderWidth: 1,
                }]
              }} 
              options={options}
            />
          )}
        </>
    )
}

export default WeekOrdersPie
