//           'rgba(255, 159, 64, 1)',
//         ],
//         borderWidth: 1,
//       },
//     ],
//   }
  
//   const options = {
//     scales: {
//       yAxes: [
//         {
//           ticks: {
//             beginAtZero: true,
//           },
//         },
//       ],
//     },
//   }

const WeekOrdersPie = () => {
    let [labelsArray, setLabelsArray]  = useState([])
    //const colors = ['#CA5C00', '#E58943', '#F1B166','#F1D1A2','#FCE4D8','#EBD3C2']
    const dispatch = useDispatch();

    const dashboardOrdersData = useSelector(state => state.dashboardOrdersData)
    const { loading, ordersdata} = dashboardOrdersData
    
    

    useEffect(()=>{
        if(!ordersdata){
            dispatch(getDashboardOrdersData())
            // eslint-disable-next-line
        }else{
            //ordersdata.map(o => setLabelsArray(...labelsArray, o.date))
            console.log('hi')
            let b = []
            ordersdata.map(o => b.push(o.date))
            set(b)
        }

    },[dispatch, ordersdata])

    

    return (
        <> 
          <div className='header'>
            <h3 className='title'>This week orders</h3>
          </div>
          { loading ? ( <Loader />) :(
            
            <Bar 
              data={{
                labels: labelsArray,
              }} 
              //options={options} 
            />
          )}
        </>
    )
}

export default WeekOrdersPie
