import React,{ useEffect } from 'react'
import { Bar } from 'react-chartjs-2'
import Loader from '../components/Loader';
import { useDispatch, useSelector } from 'react-redux';
import { getDashboardOrdersData } from '../actions/dashboardAction'

const WeekOrdersPie = () => {
    const labelsArray  = ['Su','Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa']
    const colors = ['#3F3D3B', '#555251', '#6A6866','#7F7D7C','#959392','#AAA9A8']
    const options = {
      scales: {
        yAxes: [
          {
            ticks: {
              beginAtZero: true,
            },
          },
        ],
      },
    }
    const dispatch = useDispatch();

    const dashboardOrdersData = useSelector(state => state.dashboardOrdersData)
    const { loading, ordersdata} = dashboardOrdersData
    
    useEffect(()=>{
      dispatch(getDashboardOrdersData())
      // eslint-disable-next-line
    },[dispatch])

    return (
        <> 
          <div className='header text-center'>
            <h4 className='title'>This week orders</h4>
          </div>
          { loading ? ( <Loader />) :(
            
            <Bar 
              data={{
                labels: labelsArray,
                datasets: [{
                  label: '# orders',
                  data: ordersdata,
                  backgroundColor: colors,
                  borderColor: colors,
                  borderWidth: 1,
                }]
              }} 
              options={options}
            />
          )}
        </>
    )
}

export default WeekOrdersPie
